import {
  Component,
  Input,
  ChangeDetectionStrategy,
  ViewEncapsulation,
  Output,
  EventEmitter,
} from '@angular/core';

export type TZyfraButtonIconPosision = 'left' | 'right' | 'top' | 'bottom';

@Component({
  selector: 'zyfra-button',
  templateUrl: './zyfra-button.component.html',
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ZyfraButtonComponent {
  @Input() label: string;
  @Input() type = 'button';
  @Input() icon: string;
  @Input() iconPos: TZyfraButtonIconPosision = 'left';
  @Input() disabled: boolean;
  @Input() badge: string;
  @Input() style: string;
  @Input() styleClass: string;

  @Output() onClick = new EventEmitter<PointerEvent>();
  @Output() onFocus = new EventEmitter<FocusEvent>();
  @Output() onBlur = new EventEmitter<FocusEvent>();

  //    ( .  HostListener  )
  nativeClick(event: Event): void {
    if (this.disabled) {
      event.stopPropagation();
    }
  }

  click(event: PointerEvent): void {
    this.onClick.emit(event);
  }

  focus(event: FocusEvent): void {
    this.onFocus.emit(event);
  }

  blur(event: FocusEvent): void {
    this.onBlur.emit(event);
  }
}

import {
  Component,
  Input,
  Output,
  EventEmitter,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  forwardRef,
  Provider,
} from '@angular/core';
import { NG_VALUE_ACCESSOR, ControlValueAccessor } from '@angular/forms';
import { Checkbox } from 'primeng/checkbox';

const CHECKBOX_VALUE_ACCESSOR: Provider = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => ZyfraCheckboxComponent),
  multi: true,
};

@Component({
  selector: 'zyfra-checkbox',
  templateUrl: './zyfra-checkbox.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [CHECKBOX_VALUE_ACCESSOR],
  styles: [],
})
export class ZyfraCheckboxComponent extends Checkbox implements ControlValueAccessor {
  @Input() binary = true;
  @Input() checkboxIcon = 'zyfra-icon selection-check-simple';
  @Input() model: boolean;
  @Output() modelChange = new EventEmitter<boolean | null>();

  constructor(cd: ChangeDetectorRef) {
    super(cd);
  }

  onChangeHandler(event: { checked: boolean; originalEvent: PointerEvent }) {
    if (this.formControl) {
      this.formControl.setValue(event.checked);
    } else {
      this.model = event.checked;
    }
    this.modelChange.emit(this.model);
  }
}

