  return cloneArrayOfObjects(ProductsOrdersSmall);
}

import { DOCUMENT } from '@angular/common';
import { ElementRef, Inject, Injectable } from '@angular/core';

interface Dimensions {
  width: number;
  height: number;
}

@Injectable()
export class ZyfraDropdownWithContentService {
  constructor(@Inject(DOCUMENT) private document: Document, private element: ElementRef) {}

  public setDropdownPanelPosition(): void {
    const dropdownPanel: HTMLElement = this.element.nativeElement.querySelector('.p-dropdown-panel');
    const targetHeightElement: HTMLElement = this.element.nativeElement.firstElementChild;
    dropdownPanel.style.visibility = 'hidden';

    setTimeout(() => {
      this.relativePosition(dropdownPanel, targetHeightElement);
      dropdownPanel.style.visibility = 'visible';
    });
  }

  // https://github.com/primefaces/primeng/blob/master/src/app/components/dom/domhandler.ts#L94
  private relativePosition(dropdownPanelElement: HTMLElement, target: HTMLElement): void {
    const elementDimensions = dropdownPanelElement.offsetParent
      ? { width: dropdownPanelElement.offsetWidth, height: dropdownPanelElement.offsetHeight }
      : this.getHiddenElementDimensions(dropdownPanelElement);

    const targetHeight = target.offsetHeight;
    const targetOffset = target.getBoundingClientRect();
    const viewport = this.getViewport();

    let top: number, left: number;

    if (targetOffset.top + targetHeight + elementDimensions.height > viewport.height) {
      top = -1 * elementDimensions.height;
      dropdownPanelElement.style.transformOrigin = 'bottom';
      if (targetOffset.top + top < 0) {
        top = -1 * targetOffset.top;
      }
    } else {
      top = targetHeight;
      dropdownPanelElement.style.transformOrigin = 'top';
    }

    if (elementDimensions.width > viewport.width) {
      // element wider then viewport and cannot fit on screen (align at left side of viewport)
      left = targetOffset.left * -1;
    } else if (targetOffset.left + elementDimensions.width > viewport.width) {
      // element wider then viewport but can be fit on screen (align at right side of viewport)
      left = (targetOffset.left + elementDimensions.width - viewport.width) * -1;
    } else {
      // element fits on screen (align with target)
      left = 0;
    }

    dropdownPanelElement.style.top = top + 'px';
    dropdownPanelElement.style.left = left + 'px';
  }

  private getHiddenElementDimensions(element: HTMLElement): Dimensions {
    const dimensions: Dimensions = {
      width: 0,
      height: 0,
    };
    element.style.visibility = 'hidden';
    element.style.display = 'block';
    dimensions.width = element.offsetWidth;
    dimensions.height = element.offsetHeight;
    element.style.display = 'none';
    element.style.visibility = 'visible';

    return dimensions;
  }

  private getViewport(): Dimensions {
    const win = this.document.defaultView,
      d = this.document,
      e = d.documentElement,
      g = d.getElementsByTagName('body')[0],
      w = win.innerWidth || e.clientWidth || g.clientWidth,
      h = win.innerHeight || e.clientHeight || g.clientHeight;

    return { width: w, height: h };
  }
}

import {
  AfterContentInit,
  ChangeDetectionStrategy,
  Component,
  ContentChildren,
  EventEmitter,
  Input,
  Output,
  QueryList,
  TemplateRef,
  ViewChild,
} from '@angular/core';
import { AnimationEvent } from '@angular/animations';
import { ControlValueAccessor } from '@angular/forms';
import { Dropdown } from 'primeng/dropdown';
import { WrappedFormComponent } from '../@core/value-accessor/wrapped-form.component';
import { ZyfraDropdownTemplateDirective } from './zyfra-dropdown-template.directive';

export interface DropdownChangeEvent<T> {
  originalEvent: PointerEvent;
  value: T;
}

@Component({
  selector: 'zyfra-dropdown',
  templateUrl: './zyfra-dropdown.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ZyfraDropdownComponent<T = unknown>
  extends WrappedFormComponent
  implements ControlValueAccessor, AfterContentInit
{
  @Input() options: T[];
  @Input() optionLabel = 'label';
  @Input() optionValue;
  @Input() optionDisabled = 'disabled';
  @Input() optionGroupLabel = 'label';
  @Input() optionGroupChildren = 'items';
  @Input() name: string;
  @Input() scrollHeight = '200px';
  @Input() style: string;
  @Input() panelStyle: string;
  @Input() styleClass: string;
  @Input() panelStyleClass: string;
  @Input() filter: boolean;
  @Input() filterValue: string;
  @Input() filterBy: string;
  @Input() filterMatchMode:
    | 'contains'
    | 'startsWith'
    | 'endsWith'
    | 'equals'
    | 'notEquals'
    | 'in'
    | 'lt'
    | 'lte'
    | 'gt'
    | 'gte' = 'contains';
  @Input() filterPlaceholder: string;
  @Input() filterLocale: string;
  @Input() required: boolean;
  @Input() disabled: boolean; // TODO(rustam) remove this prop use control disable
  @Input() readonly: boolean;
  @Input() emptyMessage = 'No records found';
  @Input() emptyFilterMessage = 'No results found';
  @Input() ariaLabelledBy: string;
  @Input() editable: boolean;
  @Input() maxlength: number;
  @Input() appendTo: any;
  @Input() tabindex: number;
  @Input() placeholder: string;
  @Input() inputId: string;
  @Input() dataKey: string;
  @Input() autofocus: boolean;
  @Input() autofocusFilter: boolean;
  @Input() resetFilterOnHide: boolean;
  @Input() dropdownIcon = 'zyfra-icon chevrons-down';
  @Input() autoDisplayFirst = true;
  @Input() group: boolean;
  @Input() groupTemplateClass: string;
  @Input() showClear: boolean;
  @Input() baseZIndex = 0;
  @Input() autoZIndex = true;
  @Input() showTransitionOptions = '.12s cubic-bezier(0, 0, 0.2, 1)';
  @Input() hideTransitionOptions = '.1s linear';
  @Input() ariaFilterLabel: string;
  @Input() tooltip: any;
  @Input() tooltipStyleClass: string;
  @Input() tooltipPosition = 'top';
  @Input() tooltipPositionStyle = 'absolute';
  @Input() itemSize = 30;
  @Input() label = '';
  @Input() virtualScroll: boolean;
  @Input() mini: boolean;

  @Output() onChange = new EventEmitter<DropdownChangeEvent<T>>();
  @Output() onClick = new EventEmitter<PointerEvent>();
  @Output() onFocus = new EventEmitter<FocusEvent>();
  @Output() onBlur = new EventEmitter<FocusEvent>();
  @Output() onShow = new EventEmitter<AnimationEvent>();
  @Output() onHide = new EventEmitter<AnimationEvent>();

  @ViewChild('dropdown') dropdown: Dropdown;
  @ContentChildren(ZyfraDropdownTemplateDirective) templates: QueryList<ZyfraDropdownTemplateDirective>;

  itemTemplate: TemplateRef<unknown>;
  groupTemplate: TemplateRef<unknown>;
  selectedItemTemplate: TemplateRef<unknown>;
  headerTemplate: TemplateRef<unknown>;
  emptyTemplate: TemplateRef<unknown>;
  emptyfilterTemplate: TemplateRef<unknown>;
  footerTemplate: TemplateRef<unknown>;

  ngAfterContentInit(): void {
    this.templates.forEach(item => {
      switch (item.getType()) {
        case 'item':
          this.itemTemplate = item.template;
          break;
        case 'group':
          this.groupTemplate = item.template;
          break;
        case 'selectedItem':
          this.selectedItemTemplate = item.template;
          break;
        case 'header':
          this.headerTemplate = item.template;
          break;
        case 'empty':
          this.emptyTemplate = item.template;
          break;
        case 'emptyfilter':
          this.emptyfilterTemplate = item.template;
          break;
        case 'footer':
          this.footerTemplate = item.template;
          break;
      }
    });
  }

  public override setDisabledState(): void {
    // do nothing, because have `disabled` input
  }
}

