import {
  AfterViewInit,
  ChangeDetectionStrategy,
  Component,
  ContentChildren,
  EventEmitter,
  Input,
  OnDestroy,
  OnInit,
  Output,
  QueryList,
  ViewChild,
} from '@angular/core';
import { Accordion } from 'primeng/accordion';
import { ZyfraAccordionTabComponent } from '../zyfra-accordion-tab/zyfra-accordion-tab.component';
import { Subscription } from 'rxjs';

export interface ZyfraAccordionOpenClose {
  originalEvent: PointerEvent;
  index: number;
}

@Component({
  selector: 'zyfra-accordion',
  templateUrl: './zyfra-accordion.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [
    {
      provide: Accordion,
      useFactory: (zyfraAccordion: ZyfraAccordionComponent): Accordion => zyfraAccordion.accordion,
      deps: [ZyfraAccordionComponent],
    },
  ],
})
export class ZyfraAccordionComponent implements AfterViewInit, OnDestroy, OnInit {
  /** allow multiple tabs be active at the same time. */
  @Input() multiple = false;
  /** inline style */
  @Input() style: { [key: string]: string | number | null };
  /** css class */
  @Input() styleClass: string;
  /** expand icon class */
  @Input() expandIcon = 'zyfra-icon chevrons-double-up';
  /** collapse icon class */
  @Input() collapseIcon = 'zyfra-icon chevrons-double-down';

  /** EventEmitter  */
  @Output() activeIndexChange: EventEmitter<number | number[]> = new EventEmitter();
  /** EventEmitter on closing tab */
  @Output() onClose: EventEmitter<ZyfraAccordionOpenClose> = new EventEmitter();
  /** EventEmitter on opening tab */
  @Output() onOpen: EventEmitter<ZyfraAccordionOpenClose> = new EventEmitter();

  /** primeng Accordion component */
  @ViewChild(Accordion, { static: true }) accordion: Accordion;
  /** zyfra AccordionTab component */
  @ContentChildren(ZyfraAccordionTabComponent) tabs: QueryList<ZyfraAccordionTabComponent>;

  private tabs$: Subscription;

  ngOnInit(): void {
    //   group-panels   
    this.accordion.collapseIcon = this.collapseIcon;
    this.accordion.expandIcon = this.expandIcon;
  }

  ngAfterViewInit(): void {
    this.tabs$ = this.tabs.changes.subscribe(() => this.reInitTab());
    this.reInitTab();
  }

  public reInitTab(): void {
    const primengAccordionTabs = this.tabs.toArray().map((t) => t.tab);
    this.accordion.tabList.reset(primengAccordionTabs);
    this.accordion.tabList.notifyOnChanges();
  }

  ngOnDestroy(): void {
    this.tabs$?.unsubscribe();
  }
}

import {
  AfterContentInit,
  AfterViewInit,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ContentChildren,
  EventEmitter,
  Input,
  Output,
  QueryList,
  TemplateRef,
  ViewChild,
} from '@angular/core';
import { ZyfraTemplateDirective } from '../../@core/shared/zyfra-template.directives';
import { AccordionTab } from 'primeng/accordion';

@Component({
  selector: 'zyfra-accordion-tab',
  templateUrl: './zyfra-accordion-tab.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  styles: [
    `
      :host {
        display: contents;
      }
    `,
  ],
})
export class ZyfraAccordionTabComponent implements AfterContentInit, AfterViewInit {
  /** Title of the tab */
  @Input() header: string;
  /** Defines whether the tab can be selected */
  @Input() disabled: boolean;
  /** Whether a lazy loaded panel should avoid getting loaded again on reselection */
  @Input() cache = true;
  /** Transition options of the animation */
  @Input() transitionOptions = '200ms cubic-bezier(0.86, 0, 0.07, 1)';
  /** Defines if the tab is active */
  @Input() selected: boolean;
  /** tab activeness */
  @Output() selectedChange: EventEmitter<unknown> = new EventEmitter();

  @ContentChildren(ZyfraTemplateDirective) templates: QueryList<ZyfraTemplateDirective>;
  @ViewChild(AccordionTab) tab: AccordionTab;

  headerFacet: TemplateRef<unknown>;
  contentFacet: TemplateRef<unknown>;
  iconsFacet: TemplateRef<unknown>;

  constructor(private cd: ChangeDetectorRef) {}

  ngAfterContentInit(): void {
    this.templates.forEach((t) => {
      if (t.zyfraTemplate === 'header') {
        this.headerFacet = t.templateRef;
      } else if (t.zyfraTemplate === 'icons') {
        this.iconsFacet = t.templateRef;
      } else {
        this.contentFacet = t.templateRef;
      }
    });
    if (!!this.iconsFacet && !this.headerFacet) {
      throw new Error('icon template can only be used with header template');
    }
  }

  ngAfterViewInit(): void {
    this.cd.detectChanges();
  }
}

import {
  AfterContentInit,
  ChangeDetectionStrategy,
  Component,
  ContentChildren,
  EventEmitter,
  Input,
  Output,
  QueryList,
  TemplateRef
} from '@angular/core';
import { AnimationEvent } from '@angular/animations';
import { ControlValueAccessor } from '@angular/forms';
import { ZyfraTemplateDirective } from '../@core/shared/zyfra-template.directives';
import { WrappedFormComponent } from '../@core/value-accessor/wrapped-form.component';

@Component({
  selector: 'zyfra-auto-complete',
  templateUrl: './zyfra-auto-complete.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ZyfraAutoCompleteComponent<T = unknown>
  extends WrappedFormComponent
  implements ControlValueAccessor, AfterContentInit
{
  @Input() suggestions: T[];
  @Input() field: string;
  @Input() scrollHeight = '200px';
  @Input() dropdown: boolean;
  @Input() multiple: boolean;
  @Input() dropdownIcon = 'zyfra-icon chevrons-down';
  @Input() minLength = 1;
  @Input() delay = 300;
  @Input() completeOnFocus: boolean;
  @Input() style: string;
  @Input() inputStyle: string;
  @Input() panelStyle: string;
  @Input() styleClass: string;
  @Input() inputStyleClass: string;
  @Input() panelStyleClass: string;
  @Input() inputId: string;
  @Input() name: string;
  @Input() optionGroupLabel: string;
  @Input() group: boolean;
  @Input() optionGroupChildren = 'items';
  @Input() placeholder: string;
  @Input() readonly: boolean;
  @Input() disabled: boolean;
  @Input() maxlength: number;
  @Input() size: number;
  @Input() appendTo: any;
  @Input() tabindex: number;
  @Input() dataKey: string;
  @Input() autoHighlight: boolean;
  @Input() type = 'text';
  @Input() showEmptyMessage: boolean;
  @Input() emptyMessage = 'No records found.';
  @Input() immutable = true;
  @Input() required: boolean;
  @Input() autofocus: boolean;
  @Input() forceSelection: boolean;
  @Input() dropdownMode = 'blank';
  @Input() baseZIndex = 0;
  @Input() autoZIndex = true;
  @Input() showTransitionOptions = '.12s cubic-bezier(0, 0, 0.2, 1)';
  @Input() hideTransitionOptions = '.1s linear';
  @Input() ariaLabel: string;
  @Input() ariaLabelledBy: string;
  @Input() unique = true;
  @Input() autocomplete: string;
  @Input() virtualScroll: boolean;
  @Input() itemSize: number;
  @Input() label = '';

  @Output() completeMethod = new EventEmitter<{ originalEvent: InputEvent | PointerEvent; query: string }>();
  @Output() onFocus = new EventEmitter<FocusEvent>();
  @Output() onBlur = new EventEmitter<FocusEvent>();
  @Output() onKeyUp = new EventEmitter<KeyboardEvent>();
  @Output() onSelect = new EventEmitter<T>();
  @Output() onUnselect = new EventEmitter<unknown>();
  @Output() onDropdownClick = new EventEmitter<PointerEvent>();
  @Output() onClear = new EventEmitter<InputEvent>();
  @Output() onShow = new EventEmitter<AnimationEvent>();
  @Output() onHide = new EventEmitter<void>();

  itemTemplate: TemplateRef<unknown>;
  selectedItemTemplate: TemplateRef<unknown>;

  @ContentChildren(ZyfraTemplateDirective) templates: QueryList<ZyfraTemplateDirective>;

  ngAfterContentInit(): void {
    this.templates.forEach((template) => {
      if (template.zyfraTemplate === 'item') {
        this.itemTemplate = template.templateRef;
      } else if (template.zyfraTemplate === 'selectedItem') {
        this.selectedItemTemplate = template.templateRef;
      }
    });
  }
}

import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';
import { MenuItem } from 'primeng/api';

type ItemClickEvent = { originalEvent: Event; item: MenuItem };

@Component({
  selector: 'zyfra-breadcrumb',
