export class ZyfraBreadcrumbComponent {
  @Input() items: MenuItem[] = [];
  @Input() home: MenuItem = { icon: 'zyfra-icon social-home-breadcrumbs' };
  @Input() style: string = null;
  @Input() styleClass: string = null;

