  isOpen: boolean;

  constructor(
    injector: Injector,
    ngControl: NgControl,
    private dropdownService: ZyfraDropdownWithContentService
  ) {
    super(injector, ngControl);
  }

  ngAfterContentInit(): void {
    this.templates.forEach(item => {
      switch (item.getType()) {
        case 'item':
          this.itemTemplate = item.template;
