(args) => ({
  template: `
    <div><zyfra-checkbox label="  FormControl" [formControl]="fControl"></zyfra-checkbox></div>
    <div style="font-family: var(--fontFamily); margin-top: 20px;">( : {{fControl?.value === undefined ? 'undefined' : fControl?.value}})</div>
    <br>
    <br>
    <zyfra-button [style]="{ 'margin-right': '5px' }" label="toggle disable/enable" (click)="toggleStatus()"></zyfra-button>
    <zyfra-button label="toggle true/false" (click)="toggleValue()"></zyfra-button>
  `,
  props: {
    ...args,
    fControl: new FormControl(true),
    toggleStatus(): void {
      if (this.fControl.disabled) {
        this.fControl.enable();
      } else {
        this.fControl.disable();
      }
    },
    toggleValue(): void {
      this.fControl.setValue(!this.fControl.value);
    },
  },
})

(item: ZyfraMenuItem, parent?: TreeNode): TreeNode => {
  let children;
  const node: TreeNode = {
    label: item.label,
    data: item,
    key: generateKey(),
    parent
  };
  if (item.items) {
    children = item.items.map(childItem => {
      return convertToNode(childItem, node);
    });
  }
  node.children = children;
  return node;
}

