import { DOCUMENT } from '@angular/common';
import { ElementRef, Inject, Injectable } from '@angular/core';

interface Dimensions {
  width: number;
  height: number;
}

@Injectable()
export class ZyfraDropdownWithContentService {
  constructor(@Inject(DOCUMENT) private document: Document, private element: ElementRef) {}

  public setDropdownPanelPosition(): void {
    const dropdownPanel: HTMLElement = this.element.nativeElement.querySelector('.p-dropdown-panel');
    const targetHeightElement: HTMLElement = this.element.nativeElement.firstElementChild;
    dropdownPanel.style.visibility = 'hidden';

    setTimeout(() => {
      this.relativePosition(dropdownPanel, targetHeightElement);
      dropdownPanel.style.visibility = 'visible';
    });
  }

  // https://github.com/primefaces/primeng/blob/master/src/app/components/dom/domhandler.ts#L94
  private relativePosition(dropdownPanelElement: HTMLElement, target: HTMLElement): void {
    const elementDimensions = dropdownPanelElement.offsetParent
      ? { width: dropdownPanelElement.offsetWidth, height: dropdownPanelElement.offsetHeight }
      : this.getHiddenElementDimensions(dropdownPanelElement);

    const targetHeight = target.offsetHeight;
    const targetOffset = target.getBoundingClientRect();
    const viewport = this.getViewport();

    let top: number, left: number;

    if (targetOffset.top + targetHeight + elementDimensions.height > viewport.height) {
      top = -1 * elementDimensions.height;
      dropdownPanelElement.style.transformOrigin = 'bottom';
      if (targetOffset.top + top < 0) {
        top = -1 * targetOffset.top;
      }
    } else {
      top = targetHeight;
      dropdownPanelElement.style.transformOrigin = 'top';
    }

    if (elementDimensions.width > viewport.width) {
      // element wider then viewport and cannot fit on screen (align at left side of viewport)
      left = targetOffset.left * -1;
    } else if (targetOffset.left + elementDimensions.width > viewport.width) {
      // element wider then viewport but can be fit on screen (align at right side of viewport)
      left = (targetOffset.left + elementDimensions.width - viewport.width) * -1;
    } else {
      // element fits on screen (align with target)
      left = 0;
    }

    dropdownPanelElement.style.top = top + 'px';
    dropdownPanelElement.style.left = left + 'px';
  }

  private getHiddenElementDimensions(element: HTMLElement): Dimensions {
    const dimensions: Dimensions = {
      width: 0,
      height: 0,
    };
    element.style.visibility = 'hidden';
    element.style.display = 'block';
    dimensions.width = element.offsetWidth;
    dimensions.height = element.offsetHeight;
    element.style.display = 'none';
    element.style.visibility = 'visible';

    return dimensions;
  }

  private getViewport(): Dimensions {
    const win = this.document.defaultView,
      d = this.document,
      e = d.documentElement,
      g = d.getElementsByTagName('body')[0],
      w = win.innerWidth || e.clientWidth || g.clientWidth,
      h = win.innerHeight || e.clientHeight || g.clientHeight;

    return { width: w, height: h };
  }
}

import {
  ApplicationRef,
  ComponentFactoryResolver,
  ComponentRef,
  EmbeddedViewRef,
  Injectable,
  Injector,
  Type,
} from '@angular/core';
import {
  DynamicDialogComponent,
  DynamicDialogConfig,
  DynamicDialogInjector,
  DynamicDialogRef,
} from 'primeng/dynamicdialog';
import { ZyfraDynamicDialogRef } from '.';
import { ZyfraDynamicDialogConfig } from './zyfra-dynamic-dialog-config';

@Injectable()
export class ZyfraDynamicDialogService {
  // TODO: check why it's not exported from the module

  public dialogComponentRefMap: Map<DynamicDialogRef, ComponentRef<DynamicDialogComponent>> = new Map();
  public config: ZyfraDynamicDialogConfig;
  public ref: ZyfraDynamicDialogRef;

  constructor(
    private componentFactoryResolver: ComponentFactoryResolver,
    private appRef: ApplicationRef,
    private injector: Injector
  ) {}

  public open(componentType: Type<unknown>, config: ZyfraDynamicDialogConfig): ZyfraDynamicDialogRef {
    const dialogRef = this.appendDialogComponentToBody(config);
    this.config = config;
    this.ref = dialogRef;
    this.dialogComponentRefMap.get(dialogRef).instance.childComponentType = componentType;

    return dialogRef;
  }

  private appendDialogComponentToBody(config: DynamicDialogConfig): DynamicDialogRef {
    const map = new WeakMap();
    map.set(DynamicDialogConfig, config);

    const dialogRef = new DynamicDialogRef();
    map.set(DynamicDialogRef, dialogRef);

    const sub = dialogRef.onClose.subscribe(() => {
      this.dialogComponentRefMap.get(dialogRef).instance.close();
    });

    const destroySub = dialogRef.onDestroy.subscribe(() => {
      this.removeDialogComponentFromBody(dialogRef);
      destroySub.unsubscribe();
      sub.unsubscribe();
    });

    const componentFactory = this.componentFactoryResolver.resolveComponentFactory(DynamicDialogComponent);
    const componentRef = componentFactory.create(new DynamicDialogInjector(this.injector, map));

    this.appRef.attachView(componentRef.hostView);

    const domElem = (componentRef.hostView as EmbeddedViewRef<unknown>).rootNodes[0] as HTMLElement;
    document.body.appendChild(domElem);

    this.dialogComponentRefMap.set(dialogRef, componentRef);

    return dialogRef;
  }

  private removeDialogComponentFromBody(dialogRef: DynamicDialogRef): void {
    if (!dialogRef || !this.dialogComponentRefMap.has(dialogRef)) {
      return;
    }

    const dialogComponentRef = this.dialogComponentRefMap.get(dialogRef);
    this.appRef.detachView(dialogComponentRef.hostView);
    dialogComponentRef.destroy();
    this.dialogComponentRefMap.delete(dialogRef);
  }
}

