  --hierarchicalMenuItemAddIconSize: 22px;
  --hierarchicalMenuItemLeftPadding: 0px;
  --hierarchicalMenuMarginBetweenIconAndName: 0px;
  --hierarchicalMenuItemTextColor: white;
}

.zyfra-hierarchical-menu {
  display: flex;
  height: 100%;

  .hierarchical-menu {
    display: flex;
    position: relative;
    color: var(--hierarchicalMenuItemTextColor);
    width: 100%;
  }

  .hierarchical-menu[expanded='true'] {
    .zyfra-hierarchical-menu-list[type='sub'] {
      position: absolute;
      top: 0;
      bottom: 0;
      right: 0px;
      z-index: 0;
      transform: translateX(100%);
      height: calc(100vh - @80px);
    }
  }

  .hierarchical-menu[collapsed='true'] {
    flex-direction: column;
    overflow: auto;

    .zyfra-hierarchical-menu-list {
      overflow: initial;
    }

    &::-webkit-scrollbar {
      display: none;
    }

    .zyfra-hierarchical-menu-list[type='main'],
    .zyfra-hierarchical-menu-list[type='sub'] {
      width: var(--hierarchicalMenuItemHeight) !important;
      .hierarchical-menu-item,
      .hierarchical-menu-item__content {
        padding-left: 0 !important;
      }
    }

    .zyfra-hierarchical-menu-list[type='main'] {
      margin-bottom: @24px;
    }
  }

  .zyfra-hierarchical-menu-list[type='main'] {
    display: flex;
    flex-direction: column;
    position: relative;
    background: var(--hierarchicalMenuMainBackground);
    width: 100%;
  }

  .zyfra-hierarchical-menu-list[type='sub'] {
    display: flex;
    flex-direction: column;
    width: @360px;
    position: relative;
    background: var(--hierarchicalMenuSubBackground);
  }
}

.hierarchical-folder-menu {
  overflow: auto;
  position: absolute;
  top: 0;
  bottom: 0;
  right: 0px;
  z-index: 0;
  transform: translateX(100%);
  height: calc(100vh - @80px);
  width: 320px;
  box-shadow: 0px 4px 8px rgba(21, 23, 30, 0.15);
}

@import '../../../styles/typography/typography-const';

.zyfra-hierarchical-menu-list {
  overflow: auto;

  &[type='popover'] {
    margin-top: 2px;
    margin-left: 3px;
    border-radius: @8px;
    max-height: calc(@48px * 10);

    .hierarchical-menu-list {
      position: relative;
      background: var(--hierarchicalMenuSubBackground);
      min-width: @300px;
      max-width: max(40vh, 450px);
      color: white;
      overflow: hidden;
    }
  }
}

@import '../../../styles/typography/typography-const';

.zyfra-hierarchical-menu-item {
  .hierarchical-menu-item {
    position: relative;

    .hierarchical-menu-item__content_expanded {
      margin-left: var(--hierarchicalMenuItemLeftMargin);
    }

    &__content {
      display: flex;
      align-items: center;
      height: var(--hierarchicalMenuItemHeight);
      position: relative;
      cursor: pointer;

      &:before {
        position: absolute;
        content: '';
        bottom: 0;
        left: -1000px;
        right: 0;
        height: 1px;
        background: var(--hierarchicalMenuItemBorder);
        pointer-events: none;
      }

      &:hover:not(.hierarchical-menu-item_folder) {
        background: var(--hierarchicalMenuItemHoverBackground);
      }
    }

    &__icon {
      width: var(--hierarchicalMenuItemAddIconWrapperSize);
      height: var(--hierarchicalMenuItemAddIconWrapperSize);
      display: flex;
      align-items: center;
      justify-content: center;
      color: var(--hierarchicalMenuItemIconColor);
      font-size: var(--hierarchicalMenuItemAddIconSize);
      flex-shrink: 0;
      border-radius: @4px;
    }

    &__icon_main {
      width: var(--hierarchicalMenuItemMainIconWrapperSize);
      height: var(--hierarchicalMenuItemMainIconWrapperSize);
      font-size: var(--hierarchicalMenuItemMainIconSize);
      margin-right: var(--hierarchicalMenuMarginBetweenIconAndName);
    }

    &__icon_add {
      cursor: pointer;
      &:hover {
        color: var(--hierarchicalMenuItemIconActiveColor);
      }
    }

    &__icon_active {
      color: var(--hierarchicalMenuItemIconActiveColor);
    }

    &__label {
      user-select: none;
      flex: 1 1 auto;
      white-space: nowrap;
      text-overflow: ellipsis;
      overflow: hidden;
      min-width: 0;
      line-height: 2;
    }

    &__suffix {
      display: flex;
      align-items: center;
      flex-shrink: 0;
      padding: 0 @12px;
    }

    &.hm-item-options {
      &:hover {
        background: none;
      }
    }

    &__options {
      display: flex;
      align-items: center;
      justify-content: flex-end;
      height: var(--hierarchicalMenuItemHeight);
      position: relative;
      padding: 0 @12px;

      &:before {
        position: absolute;
        content: '';
        bottom: 0;
        left: -1000px;
        right: 0;
        height: 1px;
        background: var(--hierarchicalMenuItemBorder);
        pointer-events: none;
      }
    }
  }

  .hierarchical-menu-item[active='true'] > .hierarchical-menu-item__content {
    background: var(--hierarchicalMenuItemActiveBackground) !important;
  }

  .hierarchical-menu-item__icon_open-options {
    display: none;
  }

  .hierarchical-menu-item__icon_open-options.hierarchical-menu-item__icon_active {
    display: flex;
  }

  .hierarchical-menu-item__content:hover .hierarchical-menu-item__icon_open-options {
    display: flex;
  }

  .settings-more-vert {
    font-size: 1.5rem;
  }
  .rotate-90 {
    transform: rotate(-90deg);
  }
}

