import {
  AfterContentInit,
  AfterViewInit,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ContentChildren,
  ElementRef,
  EventEmitter,
  Input,
  OnDestroy,
  Output,
  QueryList,
  Renderer2,
  TemplateRef,
  ViewChild,
  ViewContainerRef
} from '@angular/core';
import { TreeNode } from 'primeng/api';
import { ZyfraNavMenuGroupDirective } from '../../directives/nav-menu-group.directive';
import { Router } from '@angular/router';
import { ZyfraMenuItem } from '../../model/zyfra-menu-item.interface';
import { Subject } from 'rxjs';
import { convertToNode } from '../../utils/treeNode.functions';
import {
  DEFAULT_SETTINGS,
  HeaderConfig,
  NavMenuEvent,
  NavMenuSelectionEvent,
  SettingsConfig,
  ToolbarConfig
} from '../../model/nav-menu-config';
import { ZyfraTemplateDirective } from '../../../@core/shared/zyfra-template.module';

@Component({
  selector: 'zyfra-nav-menu',
  templateUrl: './zyfra-nav-menu.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ZyfraNavMenuComponent implements AfterViewInit, AfterContentInit, OnDestroy {
  @ViewChild('navMenu') menu: ElementRef;
  @ContentChildren(ZyfraTemplateDirective) templates: QueryList<ZyfraTemplateDirective>;
  @ContentChildren(ZyfraNavMenuGroupDirective) menuGroups: QueryList<ZyfraNavMenuGroupDirective>;
  @Input() menuTitle: string;
  @Input() emptyMessage: string;
  @Input() searchPlaceholder: string = '';
  @Input() toolbarConfig: ToolbarConfig;

  @Input() get settingsConfig(): SettingsConfig {
    return this._settings;
  }

  set settingsConfig(settings: SettingsConfig) {
    if (!settings) {
      return;
    }
    this._settings = { ...DEFAULT_SETTINGS, ...settings };
  };

  @Input() headerConfig: HeaderConfig = {
    settings: false,
    home: false
  };

  @Input() set model(items: ZyfraMenuItem[]) {
    this.nodes = (items || []).map(item => convertToNode(item));
  }

  @Input() set nodes(nodes: TreeNode[]) {
    this._nodes = nodes;
  }

  get nodes(): TreeNode[] {
    return this._nodes;
  }

  @Output() homeClick = new EventEmitter<void>();
  @Output() selectionChange = new EventEmitter<NavMenuSelectionEvent>();
  @Output() nodeExpand = new EventEmitter<NavMenuEvent>();
  @Output() nodeCollapse = new EventEmitter<NavMenuEvent>();
  @Output() activeItemChange = new EventEmitter<NavMenuEvent>();


  headerExtraTemplate: TemplateRef<any>;
  toolbarExtraTemplate: TemplateRef<any>;
  itemExtraTemplate: TemplateRef<any>;

  settingsPanelConfig: SettingsConfig;
  activeNode: TreeNode;
  groupNodes: TreeNode[][] = [];
  activeGroup: number;

  private _activeNode: TreeNode;
  private _settings: SettingsConfig = DEFAULT_SETTINGS;
  private _nodes: TreeNode[] = [];
  private destroy$ = new Subject();

  constructor(private renderer: Renderer2,
              private viewContainerRef: ViewContainerRef,
              private cdr: ChangeDetectorRef,
              private router: Router
  ) {
  }

  ngAfterContentInit(): void {
    if (this.menuGroups) {
      this.menuGroups.forEach(({ items }: ZyfraNavMenuGroupDirective) => {
        this.groupNodes.push(items.map((item: ZyfraMenuItem) => convertToNode(item)));
      });
    }

    this.templates.forEach((item) => {
      switch (item.zyfraTemplate) {
        case 'header':
          this.headerExtraTemplate = item.templateRef;
          break;
        case 'toolbar':
          this.toolbarExtraTemplate = item.templateRef;
          break;
        case 'menuitem':
          this.itemExtraTemplate = item.templateRef;
          break;
        default:
          break;
      }
    });
  }

  ngAfterViewInit(): void {
    let activeGroup;
    if (this.groupNodes.length) {
      this.groupNodes.some((nodes, groupIndex) =>
        nodes.some((node) => {
          const isActiveGroup = this.hasActive(node);
          if (isActiveGroup) {
            activeGroup = groupIndex;
          }
          return isActiveGroup;
        }));
    } else {
      this.nodes.some(node => this.hasActive(node));
    }
    requestAnimationFrame(() => {
      this.activeNode = this._activeNode;
      this.activeGroup = activeGroup;
      if (this.activeNode) {
        this.expandBranchFrom(this.activeNode);
        setTimeout(() => this.scrollToActiveElement(), 500);
      }
    });
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  public nodeCollapseHandler(node: TreeNode, groupIndex?: number): void {
    this.nodeCollapse.emit({ item: node.data, groupIndex });
  }

  public nodeExpandHandler(node: TreeNode, groupIndex?: number): void {
    this.nodeExpand.emit({ item: node.data, groupIndex });
  }

  public updateActiveItem(node: TreeNode, groupIndex?: number): void {
    this.activeItemChange.emit({ item: node.data, groupIndex });
  }

  public copyConfig(): void {
    this.settingsPanelConfig = { ...this.settingsConfig };
  }

  public applySettings(): void {
    this.settingsConfig = this.settingsPanelConfig;
  }

  public selectionChangeHandler(selection: TreeNode<ZyfraMenuItem> | TreeNode<ZyfraMenuItem>[], groupIndex?: number): void {
    let selectionData;
    if (selection) {
      selectionData = Array.isArray(selection)
        ? selection.map(node => node.data)
        : selection.data;
    }
    this.selectionChange.emit({ selection: selectionData, groupIndex });
  }

  private hasActive(node: TreeNode): boolean {
    if (this.isSelected(node)) {
      return true;
    }
    if (node.children) {
      return node.children.some(childNode => this.isSelected(childNode) || this.hasActive(childNode));
    }
    return this.isSelected(node);
  }

  private isSelected(node: TreeNode): boolean {
    if (node.data.routerLink) {
      const isActive = this.isActive(node.data);
      if (isActive) {
        node.styleClass = 'active';
        this._activeNode = node;
      }
      return isActive;
    }
    return false;
  }

  private isActive(item: ZyfraMenuItem): boolean {
    return item.routerLink && this.router.isActive(this.router.createUrlTree(item.routerLink), {
      paths: 'subset',
      queryParams: 'subset',
      fragment: 'ignored',
      matrixParams: 'ignored'
    });
  }

  private expandBranchFrom(node: TreeNode): void {
    if (node.parent) {
      node.parent.expanded = true;
      this.expandBranchFrom(node.parent);
    } else {
      node.expanded = true;
    }
    this.cdr.markForCheck();
  }

  private scrollToActiveElement(): void {
    const activeItem = this.activeElement();
    if (activeItem) {
      activeItem.scrollIntoView({ block: 'center', behavior: 'smooth' })
    }
  }

  private activeElement(): HTMLElement {
    return this.menu.nativeElement.querySelector('.p-treenode.active');
  }
}

import {
  AfterViewInit,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  EventEmitter,
  Input,
  OnDestroy,
  OnInit,
  Output,
  Renderer2,
  TemplateRef,
  ViewChild,
  ViewContainerRef
} from '@angular/core';
import { Tree } from 'primeng/tree';
import { TreeNode } from 'primeng/api';
import { NavMenuService } from '../../service/selection.service';
import { filter, takeUntil } from 'rxjs/operators';
import { Subject } from 'rxjs';
import { fromRubricatorNodes, getPath } from '../../utils/treeNode.functions';
import {
  DEFAULT_TOOLBAR_CONFIG,
  SelectionType,
  ToolbarConfig,
  ViewMode
} from '../../model/nav-menu-config';

@Component({
  selector: 'zyfra-menu-group-content',
  templateUrl: './zyfra-nav-menu-group.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ZyfraNavMenuGroupComponent implements OnInit, AfterViewInit, OnDestroy {
  @ViewChild(Tree) public tree: Tree;
  @ViewChild('folderIcons') public folderIcons: TemplateRef<any>;
  @Input() itemExtra: TemplateRef<any>;
  @Input() toolbarExtra: TemplateRef<any>;
  @Input() emptyMessage: string;
  @Input() search: boolean = false;
  @Input() searchPlaceholder: string;

  @Input() set activeNode(node: TreeNode) {
    if (!node) {
      return;
    }
    this.currentFolder = node.parent;
    this.setActiveNode(node);
  }

  get activeNode(): TreeNode {
    return this._activeNode;
  }

  @Input() set nodes(nodes: TreeNode[]) {
    if (!nodes) {
      return;
    }

    this._nodes = nodes;
    this.viewNodes = nodes;
    if (this.toolbarConfig.rubricatorMode) {
      this.rubricatorNodes = fromRubricatorNodes(this.nodes);
    }
  }

  get nodes(): TreeNode[] {
    return this._nodes;
  }

  @Input() set selectionMode(mode: SelectionType) {
    this._selectionMode = mode;
    if (this.tree && this.tree.selection) {
      if (Array.isArray(this.tree.selection) && this._selectionMode === 'single') {
        this.setActiveNode(this.activeNode);
      }
      if (!Array.isArray(this.tree.selection) && this._selectionMode === 'checkbox') {
        this.tree.selection = [this.tree.selection];
      }
    }
  }

  get selectionMode(): SelectionType {
    return this._selectionMode;
  }

  @Input() set toolbarConfig(config: ToolbarConfig) {
    if (!config) {
      return;
    }
    this._toolbarConfig = { ...DEFAULT_TOOLBAR_CONFIG, ...config };
    this.isToolbarNotEmpty = Object.values(this._toolbarConfig).some(value => value);
  }

  get toolbarConfig(): ToolbarConfig {
    return this._toolbarConfig;
  }

  @Output() selectionChange: EventEmitter<TreeNode | TreeNode[]> = new EventEmitter();
  @Output() activeItemChange: EventEmitter<TreeNode> = new EventEmitter();
  @Output() nodeExpand: EventEmitter<TreeNode> = new EventEmitter();
  @Output() nodeCollapse: EventEmitter<TreeNode> = new EventEmitter();

  isToolbarNotEmpty: boolean = false;
  viewNodes: TreeNode[] = [];
  viewMode: ViewMode = 'hierarchy';
  currentFolder: TreeNode = null;

  private _selectionMode: SelectionType = 'single';
  private _nodes: TreeNode[] = [];
  private _activeNode: TreeNode;
  private _toolbarConfig: ToolbarConfig = DEFAULT_TOOLBAR_CONFIG;
  private activeRootNode: TreeNode;
  private treeWrapper: Element;
  private rubricatorNodes: TreeNode[] = [];
  private pathToActiveNode = [];
  private folderPath = [];
  private destroyed$ = new Subject();

  constructor(private renderer: Renderer2,
              private viewContainerRef: ViewContainerRef,
              private cdr: ChangeDetectorRef,
              private navMenuService: NavMenuService
  ) { }

  ngOnInit(): void {
    this.navMenuService.selection$
      .pipe(
        filter(() => Boolean(this.tree)),
        takeUntil(this.destroyed$)
      )
      .subscribe(value => {
        if (value !== this.activeNode) {
          this.resetActiveParent();
          if (this.selectionMode === 'single') {
            if (this.tree.selection) {
              this.tree.selection = null;
              this.selectionChange.emit(this.tree.selection);
            }
          }
        }
      });
  }

  ngAfterViewInit(): void {
    const embeddedViewRef = this.viewContainerRef.createEmbeddedView(this.folderIcons);
    this.treeWrapper = this.tree.el.nativeElement.querySelector('.p-tree-wrapper');
    this.renderer.appendChild(this.treeWrapper, embeddedViewRef.rootNodes[0]);
  }

  ngOnDestroy(): void {
    this.destroyed$.next();
    this.destroyed$.complete();
  }

  public selectionChangeHandler(selection: TreeNode | TreeNode[]): void {
    this.selectionChange.emit(selection);
  };

  public nodeCollapseHandler(node: TreeNode): void {
    this.nodeCollapse.emit(node);
    this.currentFolder = node.parent;
    this.updateIfHasActiveChild(node);
  }

  public nodeExpandHandler(node: TreeNode): void {
    if (node === this.activeRootNode) {
      this.changeActiveParent();
    }
    this.setCurrentNode(node);
    this.nodeExpand.emit(node);
  }

  public setActiveNode(node: TreeNode): void {
    this.resetActiveParent();
    if (this.selectionMode === 'single') {
      this.tree.selection = node;
      this.selectionChange.emit(this.tree.selection);
      this._activeNode = node;
      this.navMenuService.selectionChange(node);
    } else {
      if (this._activeNode) {
        this._activeNode.styleClass = '';
      }
      this._activeNode = node;
      this._activeNode.styleClass = 'active';
    }
    this.pathToActiveNode = getPath(node);
    if (this.viewMode === 'folder') {
      this.pathToActiveNode = [...this.folderPath, getPath(node)[1]];
    }
    this.activeItemChange.emit(node);
  }

  public toLevelUp(): void {
    this.currentFolder.expanded = false;
    this.nodeCollapseHandler(this.currentFolder);
    this.currentFolder = this.folderPath.pop();
    this.viewNodes = this.currentFolder ? [this.currentFolder] : this.nodes;
    this.cdr.markForCheck();
  }

  public toTop(): void {
    this.collapseBranchFrom(this.currentFolder);
    this.currentFolder = null;
    this.viewNodes = this.nodes;
    this.cdr.markForCheck();
  }

  public closeAll(): void {
    if (this.viewMode === 'folder') {
      this.toTop();
    } else {
      this.collapseAllNodes();
    }
    this.currentFolder = null;
  }

  public changeViewMode(triggeredMode: ViewMode): void {
    if (this.viewMode === 'rubricator') {
      this.currentFolder = null;
    }
    this.viewMode = this.viewMode === triggeredMode
      ? 'hierarchy'
      : triggeredMode;
    this.updateViewNodes(this.viewMode);
  }

  public toggleSearch(): void {
    this.changeSearchValue('');
    this.search = !this.search;
  }

  public changeSearchValue(searchString: string): void {
    this.tree._filter(searchString);
  }

  public clearSearchField(field: HTMLInputElement): void {
    field.value = '';
    this.changeSearchValue('');
    field.focus();
  }


  private setCurrentNode(node: TreeNode): void {
    if (this.viewMode === 'folder') {
      this.folderPath.push(this.currentFolder);
      this.viewNodes = [node];
    }
    this.currentFolder = node;
  }

  private resetActiveParent(): void {
    if (this.activeRootNode && this.pathToActiveNode) {
      this.pathToActiveNode.forEach(node => {
        if (node) {
          node.styleClass = '';
        }
      });
      this.activeRootNode = null;
    }
  }

  private changeActiveParent(): void {
    if (this.activeRootNode) {
      this.activeRootNode.styleClass = '';
      this.activeRootNode = this.pathToActiveNode.find(node => node?.styleClass === 'active-root');
    }
  }

  private collapseBranchFrom(node: TreeNode): void {
    this.updateIfHasActiveChild(node);
    node.expanded = false;
    while (this.folderPath.length) {
      const parent = this.folderPath.pop();
      if (parent) {
        parent.expanded = false;
      }
      this.updateIfHasActiveChild(parent);
    }
  }

  private collapseAllNodes(excludeBranch?: TreeNode): void {
    this.viewNodes.forEach(node => {
      if (node !== excludeBranch) {
        this.collapseBranch(node);
      }
    });
  }

  private collapseBranch(node: TreeNode): void {
    node.expanded = false;
    if (node.children) {
      node.children.forEach(childNode => {
        this.updateIfHasActiveChild(childNode);
        this.collapseBranch(childNode);
      });
    }
    this.updateIfHasActiveChild(node);
  }

  private updateIfHasActiveChild(node: TreeNode): void {
    if (node && this.pathToActiveNode.includes(node)) {
      this.activeRootNode = node;
      this.activeRootNode.styleClass = 'active-root';
    }
  }

  private updateViewNodes(newMode: ViewMode): void {
    if (newMode === 'rubricator') {
      if (!this.rubricatorNodes.length) {
        this.rubricatorNodes = fromRubricatorNodes(this.nodes);
      }
      this.viewNodes = this.rubricatorNodes;
    } else if (newMode === 'folder') {
      if (this.currentFolder) {
        this.folderPath = getPath(this.currentFolder);
        this.collapseAllNodes(this.folderPath[1] || this.currentFolder);
        this.currentFolder.expanded = true;
      }
      this.viewNodes = this.currentFolder ? [this.currentFolder] : this.nodes;
    } else {
      this.viewNodes = this.nodes;
    }
    if (this.search) {
      this.toggleSearch();
    }
  }
}

import {
  ChangeDetectionStrategy,
  Component,
  EventEmitter,
  Input,
  Output,
  TemplateRef
} from '@angular/core';
import { ZyfraMenuItem } from '../../model/zyfra-menu-item.interface';

@Component({
  selector: 'zyfra-nav-menu-item',
  templateUrl: './zyfra-nav-menu-item.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ZyfraNavMenuItemComponent {
  @Input() item: ZyfraMenuItem;
  @Input() itemExtra: TemplateRef<any>;

  @Output() itemClick: EventEmitter<ZyfraMenuItem> = new EventEmitter();

  public itemClickHandler(event: MouseEvent, item: ZyfraMenuItem): void {
    if (item.disabled) {
      event.preventDefault();
      return;
    }
    if (!item.url) {
      event.preventDefault();
    }
    if (item.command) {
      item.command({
        originalEvent: event,
        item
      });
    }
    this.itemClick.emit(item);
  }

  public onItemKeyDown(event): void {
    const listItem = event.currentTarget.parentElement;
    switch (event.code) {
      case 'Space':
      case 'Enter':
        if (listItem) {
          listItem.children[0].click();
        }
        event.preventDefault();
        break;
      default:
        break;
    }
  }
}

import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output, TemplateRef } from '@angular/core';

