args => ({
  template: `
    <div><zyfra-checkbox label="  FormControl" [formControl]="formControl"></zyfra-checkbox></div>
    <div style="font-family: var(--fontFamily); margin-top: 20px;">( : {{formControl?.value === undefined ? 'undefined' : formControl?.value}})</div>
    <br>
    <br>
    <zyfra-button [style]="{ 'margin-right': '5px' }" label="toggle disable/enable" (click)="toggleStatus()"></zyfra-button>
    <zyfra-button label="toggle true/false" (click)="toggleValue()"></zyfra-button>
  `,
  props: {
    ...args,
    formControl: new FormControl(false),
    toggleStatus(): void {
      if (this.formControl.disabled) {
        this.formControl.enable();
      } else {
        this.formControl.disable();
      }
    },
    toggleValue(): void {
      this.formControl.setValue(!this.formControl.value);
    },
  },
})

args => ({
  styles: [
    `
    .page {
      width: 100%;

      height: calc(100vh - 26px);
