import { Component, ChangeDetectionStrategy, Input, Output, EventEmitter, ViewChild } from '@angular/core';
import { ZyfraDropdownComponent } from '../../dropdown';
import { ZyfraDatePickerMode } from '../model/zyfra-date-picker-mode.enum';

@Component({
  selector: 'zyfra-datepicker-mode-select',
  templateUrl: './zyfra-datepicker-mode-select.component.html',
  styleUrls: ['./zyfra-datepicker-mode-select.component.less'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ZyfraDatepickerModeSelectComponent {
  @Input() disabled: boolean;
  @Input() mode = ZyfraDatePickerMode.absolute;
  @ViewChild('dropdown') dropdown: ZyfraDropdownComponent;

  @Output() selectChanged = new EventEmitter();

  public readonly modes = [
    {
      name: ' ',
      value: ZyfraDatePickerMode.absolute,
      icon: 'date-update',
    },
    {
      name: ' ',
      value: ZyfraDatePickerMode.relative,
      icon: 'date-clock',
    },
  ];
}

import {
  Component,
  ChangeDetectionStrategy,
  Input,
  Output,
  EventEmitter,
  ChangeDetectorRef,
} from '@angular/core';
import { ZyfraLocale } from '../model/zyfra-date-picker-locale.enum';
import { ZyfraDatePickerMode } from '../model/zyfra-date-picker-mode.enum';
import { ZyfraDatePickerValueType } from '../model/zyfra-date-picker-value-type.enum';
import { ZyfraDatepickerModeSelectComponent } from '../zyfra-datepicker-mode-select/zyfra-datepicker-mode-select.component';

@Component({
  selector: 'zyfra-datepicker-multi',
  templateUrl: './zyfra-datepicker-multi.component.html',
  styleUrls: ['./zyfra-datepicker-multi.component.less'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ZyfraDatepickerMultiComponent {
  // Base from relative & absolute components
  @Input() public label: string;
  @Input() public placeholder: string;
  @Input() public disabled: boolean;
  @Input() public showClear: boolean;

  // Current component 'multi'
  @Input() mode = ZyfraDatePickerMode.absolute;

  // wrapper for relative component
  @Input() public relativeValue: string;

  // wrapper for relative component
  @Input() model: string | number | Date;
  @Input() required: boolean;
  @Input() spanClass: string;
  @Input() showDate: boolean = true;
  @Input() showTime: boolean;
  @Input() inputClass: string = '';
  @Input() monthNavigator: boolean = false;
  @Input() yearNavigator: boolean = false;
  @Input() yearRange: string = null;
  @Input() showCalendarBtn: boolean = true;
  @Input() calendarBtnLabel: string;
  @Input() hideOnDateTimeSelect: boolean = true;
  @Input() numberOfMonths: number = 1;
  @Input() selectionMode: string = 'single';
  @Input() disabledDates: Date[] = null;
  @Input() disabledDays: number[] = null;
  @Input() firstDayOfWeek: number = 1;
  @Input() showOnFocus: boolean = false;
  @Input() showWeek: boolean = false;
  @Input() locale: ZyfraLocale = ZyfraLocale.ru;
  @Input() dateFormat: string;
  @Input() timeFormat: string;
  @Input() returnFormatValue: ZyfraDatePickerValueType;

  @Output() public valueChange = new EventEmitter<any>();

  @Output() public modeChange = new EventEmitter<ZyfraDatePickerMode>();

  constructor(private readonly cdr: ChangeDetectorRef) {}

  // TODO need button with dropdown
  public onClickButtonSelect(event: MouseEvent, select: ZyfraDatepickerModeSelectComponent): void {
    if (!select.dropdown.dropdown.overlayVisible) {
      select.dropdown.dropdown.onMouseclick(event);
      event.stopPropagation();
    }
  }

  public onSelectChanged(mode: ZyfraDatePickerMode): void {
    this.modeChange.emit(mode);
    this.mode = mode;
    this.cdr.detectChanges();
  }
}

import { Component, Input, Output, EventEmitter } from '@angular/core';
import { SafeHtml } from '@angular/platform-browser';

@Component({
  selector: 'zyfra-dialog',
  templateUrl: './zyfra-dialog.component.html',
  styles: [],
})
export class ZyfraDialogComponent {
  @Input() htmlTemplate: SafeHtml;

  @Input() header: string;
  @Input() draggable: boolean = true;
  @Input() acceptVisible: boolean = true;
  @Input() keepInViewport: boolean = true;
  @Input() resizable: boolean = true;
  @Input() contentStyle: object;
  @Input() visible: boolean;
  @Input() modal: boolean;
  @Input() position: string = 'center';
  @Input() blockScroll: boolean;
  @Input() closeOnEscape: boolean;
  @Input() dismissableMask: boolean;
  @Input() rtl: boolean;
  @Input() closable: boolean = true;
  @Input() appendTo: any;
  @Input() style: string;
  @Input() styleClass: string;
  @Input() maskStyleClass: string;
  @Input() contentStyleClass: string;
  @Input() showHeader: boolean = true;
  @Input() baseZIndex: number = 0;
  @Input() autoZIndex: boolean = true;
  @Input() minX: number = 0;
  @Input() minY: number = 0;
  @Input() focusOnShow: boolean = true;
  @Input() focusTrap: boolean = true;
  @Input() maximizable: boolean;
  @Input() breakpoints: object;
  @Input() transitionOptions: string = '150ms cubic-bezier(0, 0, 0.2, 1)';
  @Input() closeIcon: string = 'zyfra-icon cancel-close';
  @Input() minimizeIcon: string = 'zyfra-icon arrows-collapse';
  @Input() maximizeIcon: string = 'zyfra-icon arrows-expand';

  /* onHide */
  @Output() onHide: EventEmitter<any> = new EventEmitter();

  onHideHandler(event) {
    this.visible = false;
    this.onHide.emit(this.visible);
  }

  /* onShow */
  @Output() onShow: EventEmitter<any> = new EventEmitter();

  onShowHandler(event) {
    this.visible = true;
    this.onShow.emit(this.visible);
  }

  /* onResizeInit */
  @Output() onResizeInit: EventEmitter<any> = new EventEmitter();

  onResizeInitHandler(event) {
    this.onResizeInit.emit(event);
  }

  /* onResizeEnd */
  @Output() onResizeEnd: EventEmitter<any> = new EventEmitter();

  onResizeEndHandler(event) {
    this.onResizeEnd.emit(event);
  }

  /* onDragEnd */
  @Output() onDragEnd: EventEmitter<any> = new EventEmitter();

  onDragEndHandler(event) {
    this.onDragEnd.emit(event);
  }

  /* onMaximize */
  @Output() onMaximize: EventEmitter<any> = new EventEmitter();

  onInit() {
    this.visible = true;
  }

  onMaximizeHandler(event) {
    this.onMaximize.emit(event);
  }
}

import {
  AfterContentInit,
  ChangeDetectionStrategy,
  Component,
  ContentChildren,
  EventEmitter,
  Input,
  Output,
  QueryList,
  TemplateRef,
  ViewChild,
} from '@angular/core';
import { AnimationEvent } from '@angular/animations';
import { ControlValueAccessor } from '@angular/forms';
import { Dropdown } from 'primeng/dropdown';
import { WrappedFormComponent } from '../@core/value-accessor/wrapped-form.component';
import { ZyfraDropdownTemplateDirective } from './zyfra-dropdown-template.directive';

export interface DropdownChangeEvent<T> {
  originalEvent: PointerEvent;
  value: T;
}

@Component({
  selector: 'zyfra-dropdown',
  templateUrl: './zyfra-dropdown.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ZyfraDropdownComponent<T = unknown>
  extends WrappedFormComponent
  implements ControlValueAccessor, AfterContentInit
{
  @Input() options: T[];
  @Input() optionLabel = 'label';
  @Input() optionValue;
  @Input() optionDisabled = 'disabled';
  @Input() optionGroupLabel = 'label';
  @Input() optionGroupChildren = 'items';
  @Input() name: string;
  @Input() scrollHeight = '200px';
  @Input() style: string;
  @Input() panelStyle: string;
  @Input() styleClass: string;
  @Input() panelStyleClass: string;
  @Input() filter: boolean;
  @Input() filterValue: string;
  @Input() filterBy: string;
  @Input() filterMatchMode:
    | 'contains'
    | 'startsWith'
    | 'endsWith'
    | 'equals'
    | 'notEquals'
    | 'in'
    | 'lt'
    | 'lte'
    | 'gt'
    | 'gte' = 'contains';
  @Input() filterPlaceholder: string;
  @Input() filterLocale: string;
  @Input() required: boolean;
  @Input() disabled: boolean;
  @Input() readonly: boolean;
  @Input() emptyMessage = 'No records found';
  @Input() emptyFilterMessage = 'No results found';
  @Input() ariaLabelledBy: string;
  @Input() editable: boolean;
  @Input() maxlength: number;
  @Input() appendTo: any;
  @Input() tabindex: number;
  @Input() placeholder: string;
  @Input() inputId: string;
  @Input() dataKey: string;
  @Input() autofocus: boolean;
  @Input() autofocusFilter: boolean;
  @Input() resetFilterOnHide: boolean;
  @Input() dropdownIcon = 'zyfra-icon chevrons-down';
  @Input() autoDisplayFirst = true;
  @Input() group: boolean;
  @Input() groupTemplateClass: string;
  @Input() showClear: boolean;
  @Input() baseZIndex = 0;
  @Input() autoZIndex = true;
  @Input() showTransitionOptions = '.12s cubic-bezier(0, 0, 0.2, 1)';
  @Input() hideTransitionOptions = '.1s linear';
  @Input() ariaFilterLabel: string;
  @Input() tooltip: any;
  @Input() tooltipStyleClass: string;
  @Input() tooltipPosition = 'top';
  @Input() tooltipPositionStyle = 'absolute';
  @Input() itemSize = 30;
  @Input() label = '';
  @Input() virtualScroll: boolean;
  @Input() mini: boolean;

  @Output() onChange = new EventEmitter<DropdownChangeEvent<T>>();
  @Output() onClick = new EventEmitter<PointerEvent>();
  @Output() onFocus = new EventEmitter<FocusEvent>();
  @Output() onBlur = new EventEmitter<FocusEvent>();
  @Output() onShow = new EventEmitter<AnimationEvent>();
  @Output() onHide = new EventEmitter<AnimationEvent>();

  @ViewChild('dropdown') dropdown: Dropdown;
  @ContentChildren(ZyfraDropdownTemplateDirective) templates: QueryList<ZyfraDropdownTemplateDirective>;

  itemTemplate: TemplateRef<unknown>;
  groupTemplate: TemplateRef<unknown>;
  selectedItemTemplate: TemplateRef<unknown>;
  headerTemplate: TemplateRef<unknown>;
  emptyTemplate: TemplateRef<unknown>;
