libs/components/src/lib/nav-menu/utils/t.defaultValue:
(item: ZyfraMenuItem, parent?: TreeNode): TreeNode => {
  let children;
  const node: TreeNode = {
    label: item.label,
    data: item,
    key: generateKey(),
    parent
  };
  if (item.items) {
    children = item.items.map(childItem => {
      return convertToNode(childItem, node);
    });
  }
  node.children = children;
  return node;
}

(nodes: TreeNode[]): TreeNode[] => {
  const sortedNodes = flatNodes(nodes).sort(sortByLabel);
  const letters = new Set(sortedNodes.map(({ label }) => label.charAt(0).toUpperCase()));
  const rubricatorNodes = [];
  letters.forEach(letter => {
    rubricatorNodes.push({
      key: letter,
      label: letter,
      data: { label: letter },
      children: sortedNodes.filter(({ label }) => label.charAt(0).toUpperCase() === letter)
    });
  });
  return rubricatorNodes;
}

(node: TreeNode, acc: TreeNode[] = []): TreeNode[] => {
  acc.push(node.parent);
  return node.parent
    ? getPath(node.parent, acc)
    : acc.reverse();
}

(a: TreeNode, b: TreeNode): number => {
  if (a.label > b.label) {
    return 1;
  }
  if (a.label < b.label) {
    return -1;
  }
  return 0;
}

